    public boolean validateUsername(String username) {
        Pattern ptr = Pattern.compile("[a-zA-Z0-9_.-]{3,}");
        return ptr.matcher(username).matches();
    }

    public boolean validatePassword(String password) {
        Pattern ptr = Pattern.compile("^(?:(?=.*\\d)(?=.*[A-Z])(?=.*[a-z])|(?=.*\\d)(?=.*[^A-Za-z0-9])(?=.*[a-z])|(?=.*[^A-Za-z0-9])(?=.*[A-Z])(?=.*[a-z])|(?=.*\\d)(?=.*[A-Z])(?=.*[^A-Za-z0-9]))(?!.*(.)\\1{2,})[A-Za-z0-9!~,;:_=?*+#.\"&§%°()\\|\\[\\]\\-\\$\\^\\@\\/]{8,32}$");
        return ptr.matcher(password).matches();
    }

